import React from "react";
import styled from "styled-components";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { Nav, Navbar, NavDropdown } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

export default function Header() {
  const style = {
    display: "flex",
    justifyContent: "space-evenly",
    backgroundColor: "blue",
    width: "100%",
    height: "auto",
    color: "white",
  };
  const style2 = {
    display: "flex",
    justifyContent: "flex-start",
    alignItems: "center",
    width: "100%",
    paddingLeft: "1em",
    fontFamily: "Dancing Script",
    fontSize: "20px",
  };

  const style4 = {
    paddingTop: "0.6em",
    paddingBottom: "0.6em",
    paddingRight: "1.5em",
    paddingLeft: "1.5em",
    margin: "1em",

    backgroundColor: "yellow",
    border: "none",
    color: "blue",
  };

  const Button = styled.button`
    padding-top: 0.2em;
    padding-bottom: 0.2em;
    padding-right: 1.5em;
    padding-left: 1.5em;
    font-family: "Dancing Script";
    font-size: 2em;
    background-color: yellow;
    border: none;
    color: blue;
    &:hover {
      cursor: pointer;
      transform: scale(0.8);
    }
  `;

  function display() {
    prompt("./about.js");
  }

  const Link1 = styled(Link)`
    color: black;
    text-decoration: none;
    margin: 1.5em;

    &:hover {
      text-decoration: none;
      color: black;
      transform: scale(1.2);
    }
  `;

  return (
    <Navbar collapseOnSelect expand="sm" fixed="top">
      <Navbar.Toggle />
      <Navbar.Collapse>
        <Nav style={style2} className="mr-auto">
          <Link1 to="/">We Cart</Link1>
          <Link1 to="about.js">About</Link1>
        </Nav>
      </Navbar.Collapse>
      <div>
        <Button onClick={display}>Cart</Button>
      </div>
    </Navbar>
  );
}
