import React from "react";
import styled from "styled-components";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { Col, Card, Carousel, Row } from "react-bootstrap/";
import A1 from "./proimg1.js";

export default class Product extends React.Component {
  constructor() {
    super();
    this.state = {
      incart: false,
      count: 0,
    };
    this.carted = this.carted.bind(this);
    this.man = this.man.bind(this);
  }

  carted() {
    this.setState((prevState) => {
      if (prevState.count === 0) {
        return {
          count: prevState.count + 1,
          incart: !prevState.incart,
        };
      } else {
        return { count: prevState.count + 1 };
      }
    });
  }

  man(event) {
    if (event.target.value === "+") {
      this.setState((prevState) => {
        return { count: prevState.count + 1 };
      });
    } else {
      this.setState((prevState) => {
        {
          if (prevState.count === 1) {
            return {
              count: prevState.count - 1,
              incart: !prevState.incart,
            };
          } else {
            return {
              count: prevState.count - 1,
            };
          }
        }
      });
    }
  }

  render() {
    const Nutton = styled(Button)`
      background-color: green;
      &:hover {
        transform: scale(1.1);
      }
    `;
    const Futton = styled(Button)`
      padding: 10px;
      margin: 10px;
      width: 20%;
    `;

    console.log(this.state);

    return (
      <Col lg={4} sm={6} xs={12}>
        <Card
          style={{
            textAlign: "center",
            margin: "1.3em",
            border: "none",
            maxHeight: "400px",
            minHeight: "400px",
          }}
        >
          <Card.Body>
            <Card.Img
              style={{
                width: "100%",
                maxHeight: "150px",
                minHeight: "150px",
                maxHeight: "150px",
              }}
              src={this.props.img}
            />
            <Card.Title>
              <h3 style={{ overflow: "hidden" }}>{this.props.name}</h3>
            </Card.Title>
            <Card.Text>
              <p>{this.props.detail}</p>
            </Card.Text>
            <Nutton
              onClick={this.carted}
              variant="secondary"
              disabled={this.state.incart ? true : false}
            >
              ADD to CART
            </Nutton>
            <br />
            <br />
            <div style={{ display: this.state.count > 0 ? "block" : "none" }}>
              <Futton onClick={this.man} value="+" variant="primary">
                +
              </Futton>
              <textarea cols="2">{this.state.count}</textarea>
              <Futton onClick={this.man} variant="primary" value="-">
                -
              </Futton>
            </div>
          </Card.Body>
        </Card>
      </Col>
    );
  }
}
