import React from "react";
import styled from "styled-components";

export default function Header() {
  const style = {
    display: "flex",
    justifyContent: "space-evenly",
    backgroundColor: "blue",
    width: "100%",
    height: "auto",
    color: "white",
  };
  const style2 = {
    display: "flex",
    justifyContent: "flex-start",
    alignItems: "center",
    width: "100%",
    paddingLeft: "2em",
    fontFamily: "Dancing Script",
    fontSize: "70px",
  };
  const style3 = {
    display: "flex",
    justifyContent: "flex-end",
    alignItems: "center",
    width: "100%",
    paddingRight: "2em",
  };
  const style4 = {
    paddingTop: "0.6em",
    paddingBottom: "0.6em",
    paddingRight: "1.5em",
    paddingLeft: "1.5em",
    margin: "1em",

    backgroundColor: "yellow",
    border: "none",
    color: "blue",
  };

  // const Button = styled.button`
  // padding-top: 0.6em;
  // padding-bottom: 0.6em;
  // padding-right: 1.5em;
  // padding-left: 1.5em;
  // margin: 1em;
  // font-family: "Dancing Script";
  // font-size: 2em;
  // background-color: yellow;
  // border: none;
  // color: blue";
  // &:hover{
  //   background-color: green;
  //   cursor: pointer;
  // }`;

  const Button = styled.button`
    background-color: black;
    color: white;
    font-size: 20px;
    padding: 10px 60px;
    border-radius: 5px;
    margin: 10px 0px;
    &:disabled {
      color: grey;
      opacity: 0.7;
      cursor: default;
      cursor: pointer;
    }
  `;

  function display() {
    window.alert("working on");
  }

  return (
    <header style={style}>
      <div style={style2}>
        <div>WE CART</div>
      </div>
      <div style={style3}>
        <Button onClick={display}>Cart</Button>
      </div>
    </header>
  );
}
