import React from "react";
import styled from "styled-components";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { Col, Card, Carousel, Row } from "react-bootstrap/";
import A1 from "./proimg1.js";

export default class Product extends React.Component {
  constructor() {
    super();
    this.state = {
      incart: false,
      count: 0,
    };
    this.carted = this.carted.bind(this);
  }

  // carted() {
  //   if (this.state.count == 0) {
  //     this.setState((prevState) => {
  //       return {
  //         count: prevState.count + 1,
  //         incart: true,
  //       };
  //     });
  //   } else if (this.state.count >= 1) {
  //     this.setState((prevState) => {
  //       return {
  //         count: prevState.count + 2,
  //         incart: true,
  //       };
  //     });
  //   }
  //   console.log(this.state);
  // }

  carted() {
    this.setState((prevState) => {
      return {
        count: prevState.count + 1,
      };
    });
  }

  render() {
    const displ = this.state.count >= 2 ? "none" : "block";

    const Nutton = styled(Button)`
      background-color: green;
      &:hover {
        transform: scale(1.1);
      }
    `;
    const Futton = styled(Button)`
      padding: 10px;
      margin: 10px;
      width: 20%;
    `;

    // const productimages = A1.map((A1) => <Poto img={A1.img} />);

    return (
      <Col lg={4} sm={6} xs={12}>
        <Card
          style={{
            textAlign: "center",
            margin: "1.3em",
            border: "none",
            maxHeight: "400px",
            minHeight: "400px",
          }}
        >
          <Card.Body>
            <Card.Img
              style={{
                width: "100%",
                maxHeight: "150px",
                minHeight: "150px",
                maxHeight: "150px",
              }}
              src={this.props.img}
            />
            <Card.Title>
              <h3 style={{ overflow: "hidden" }}>{this.props.name}</h3>
            </Card.Title>
            <Card.Text>
              <p>{this.props.detail}</p>
            </Card.Text>
            <Nutton onClick={this.carted} variant="secondary">
              ADD to CART
            </Nutton>
            <br />
            <br />
            <Futton
              style={{ display: (this.state.count = 2 ? "block" : "none") }}
              variant="primary"
            >
              +
            </Futton>
            <Futton variant="primary">-</Futton>
          </Card.Body>
        </Card>
      </Col>
    );
  }
}
