import React from "react";
import styled from "styled-components";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { Nav, Navbar } from "react-bootstrap";

export default function Header() {
  const style = {
    display: "flex",
    justifyContent: "space-evenly",
    backgroundColor: "blue",
    width: "100%",
    height: "auto",
    color: "white",
  };
  const style2 = {
    display: "flex",
    justifyContent: "flex-start",
    alignItems: "center",
    width: "100%",
    paddingLeft: "2em",
    fontFamily: "Dancing Script",
    fontSize: "40px",
  };
  const style3 = {
    display: "flex",
    justifyContent: "flex-end",
    alignItems: "center",
    width: "100%",
    paddingRight: "2em",
  };
  const style4 = {
    paddingTop: "0.6em",
    paddingBottom: "0.6em",
    paddingRight: "1.5em",
    paddingLeft: "1.5em",
    margin: "1em",

    backgroundColor: "yellow",
    border: "none",
    color: "blue",
  };

  const Button = styled.button`
    padding-top: 0.6em;
    padding-bottom: 0.6em;
    padding-right: 1.5em;
    padding-left: 1.5em;
    margin: 1em;
    font-family: "Dancing Script";
    font-size: 2em;
    background-color: yellow;
    border: none;
    color: blue;
    &:hover {
      cursor: pointer;
      transform: scale(1.2);
    }
  `;

  function display() {
    prompt("./about.js");
  }

  const Link1 = styled(Link)`
    color: black;
    text-decoration: none;
    margin: 1.5em;

    &:hover {
      text-decoration: none;
      color: black;
    }
  `;

  return (
    <header style={style}>
      <Navbar>
        <Navbar.Collapse>
          <div style={style2}>
            <div>
              <Link1 to="/">WE CART</Link1>
            </div>
            <div>
              <Link1 to="about.js">About</Link1>
            </div>
          </div>
        </Navbar.Collapse>
        <div style={style3}>
          <Button onClick={display}>Cart</Button>
        </div>
      </Navbar>
    </header>
  );
}
