import React from "react";
import styled from "styled-components";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { Col, Card, Carousel } from "react-bootstrap/";
import A1 from "./proimg1.js";
import CommonBurger from "./photo.js";

export default class Product extends React.Component {
  constructor() {
    super();
    this.state = {
      incart: false,
      count: 0,
    };
    this.carted = this.carted.bind(this);
  }

  carted() {
    if (this.state.count == 0) {
      this.setState((prevState) => {
        return {
          count: prevState.count + 1,
          incart: true,
        };
      });
    } else if (this.state.count >= 1) {
      this.setState((prevState) => {
        return {
          count: prevState.count + 2,
          incart: true,
        };
      });
    }
    console.log(this.state);
  }

  render() {
    const Nutton = styled(Button)`
      background-color: green;
      &:hover {
        transform: scale(1.1);
      }
    `;

    // const productimages = A1.map((A1) => <Poto img={A1.img} />);

    return (
      <Col lg={3} md={4} sm={6} xs={12}>
        <Card style={{ textAlign: "center", margin: "1.3em", border: "none" }}>
          <Card.Body>
            <Card.Title>
              <h3>{this.props.name}</h3>
            </Card.Title>
            <Card.Text>
              <p>{this.props.detail}</p>
            </Card.Text>
            <Nutton variant="secondary">ADD to CART</Nutton>
          </Card.Body>
        </Card>
      </Col>
    );
  }
}
