import React from "react";

export default class Product extends React.Component {
  constructor() {
    super();
    this.state = {
      incart: false,
      count: 0,
    };
    this.carted = this.carted.bind(this);
  }

  carted() {
    this.setState((prevState) => {
      if ((prevState.count = 0)) {
        return {
          count: prevState.count + 1,
          incart: true,
        };
      }
    });
  }

  render() {
    const image = require("./1.jpg");

    console.log(this.state);

    return (
      <div className="cards">
        <div className="cardimg">
          <img src={image} alt="Image will be loaded" />
        </div>
        <div className="carddet">
          <h3>{this.props.name}</h3>
          <p style={{ overflow: "hidden" }}>{this.props.detail}</p>
          <button className="b1" onClick={this.carted}>
            Add to Cart
          </button>
        </div>
      </div>
    );
  }
}
